import React from 'react'
import { Form, FormControl, Nav, NavDropdown } from 'react-bootstrap'
import {Button} from '@material-ui/core'
import Navbar from 'react-bootstrap/Navbar'
import {Avatar} from "@material-ui/core";
import './NavbarComponent.css'
import { auth, provider } from './firebase';
import { useAuthState } from 'react-firebase-hooks/auth';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import DonateComponent from './DonateComponent';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
function NavbarComponent() {
    const [user,loading]=useAuthState(auth);
    const SignIn=e=>{
        e.preventDefault();
        auth.signInWithPopup(provider).catch((error)=>alert(error.message ));

    };
    const notify=()=>{
        if(!user)
        toast.error("You need to login first ! ", {position:toast.POSITION.TOP_CENTER});
        
    };
    return (
        <div>
           
          
  <Navbar className="color-nav"  >
  
  
   
    <Nav className="mr-auto">
        
      
     {user ? (
         <>
         <Avatar style={{ marginRight:"20px"}}src={user?.photoURL}/>
         <Navbar.Brand style={{color:"#7e685a"}}href="#home">{user?.displayName}</Navbar.Brand>
      <button type="submit" onClick={()=>auth.signOut()}>Logout</button>
     </>
     ):(
         <>
         
        <Button   variant="contained" type="submit" onClick={SignIn}>Login</Button>
        </>
     ) }
        <Link to="/donate">
     <Button  className="mr-2"  variant="outline-success" onClick={notify}>Donate</Button>
     </Link>
       

     
    
      
      
      <Button   className="mr-2" variant="outline-success">Demand</Button>
      <Link to="/">
     <Button  className="mr-2"  variant="outline-success">Home</Button>
     </Link>
       
    </Nav>
    <Form inline>
      <FormControl style={{color:"#7e685a"}}type="text" placeholder="Search" className="mr-sm-2" />
      <Button variant="outline-success">Search</Button>
    </Form>
  </Navbar>
 
        </div>
    )
}

export default NavbarComponent
